mapper.select:
SELECT id, name, user_id, password, gender, email, birthdate, car_manufacturer, car_model
FROM QUSER
WHERE id = #{id}

mapper.update:
UPDATE QUSER
SET name = #{name}, user_id = #{userId}, password = #{password}, gender = #{gender}, email = #{email}, birthdate = #{birthdate}, car_manufacturer = #{carManufacturer}, car_model = #{carModel}
WHERE id = #{id}

mapper.select:
	   SELECT COUNT(*) 
	   FROM  QUSER
       WHERE USERID = #{userId}
         AND PASSWORD = #{password}

SELECT COUNT(*) 
FROM  QUSER
